package day1;

public class Mobile {
	String mname;
	int price;
	String colour;
	double capacity;
	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		Mobile M=new Mobile();
		M.mname="OPPO";
		M.price=20000;
		M.colour="black";
		M.capacity=5.89;
		System.out.println("Mobile Name : "+M.mname);
		System.out.println("Mobile Price : "+M.price);
		System.out.println("Colour : "+M.colour);
		System.out.println("Battery Capacity"+M.capacity);
		

	}

}
